import React,{Component } from "react";
import ReactDataGrid from "react-data-grid";
import CustomRowRenderer from './CustomRowRenderer';
// import { Menu } from "react-data-grid-addons";
import ReactDOM from 'react-dom';

  import { Menu } from "react-data-grid-addons";
  const { ContextMenu, MenuItem, SubMenu, ContextMenuTrigger } = Menu;
  function ExampleContextMenu({
    idx,
    id,
    rowIdx,
    onRowDelete,
    onRowInsertAbove,
    onRowInsertBelow
  }) {
    return (
      <ContextMenu id={id}>
        <MenuItem data={{ rowIdx, idx }} onClick={onRowDelete}>
          Delete Row
        </MenuItem>
        <SubMenu title="Insert Row">
          <MenuItem data={{ rowIdx, idx }} onClick={onRowInsertAbove}>
            Above
          </MenuItem>
          <MenuItem data={{ rowIdx, idx }} onClick={onRowInsertBelow}>
            Below
          </MenuItem>
        </SubMenu>
      </ContextMenu>
    );
  }
const defaultColumnProperties = {
    // resizable: true,
    frozen: true,
    width: 120
  };
  function ExampleContextMenu({
    idx,
    id,
    rowIdx,
    onRowDelete,
    onRowInsertAbove,
    onRowInsertBelow
  }) {
    return (
      <ContextMenu id={id}>
        <MenuItem data={{ rowIdx, idx }} onClick={onRowDelete}>
          Delete Row
        </MenuItem>

          <MenuItem data={{ rowIdx, idx }} onClick={onRowInsertAbove}>
            Above
          </MenuItem>
          <MenuItem data={{ rowIdx, idx }} onClick={onRowInsertBelow}>
            Below
          </MenuItem>

      </ContextMenu>
    );
  }
const columns = [
  { key: "id", name: "ID", frozen: true,sortDescendingFirst: true,headerRenderer:<ContextMenuTrigger id="some_unique_identifier">
  <div className="well">ID</div>
</ContextMenuTrigger> },
  { key: "title", name: "Title", editable: true,sortable: true, filterable: true, headerRenderer:<ContextMenuTrigger id="some_unique_identifier">
  <div className="well">title</div>
</ContextMenuTrigger>, resizable: true},
  { key: "complete", name: "Complete", editable: true,sortable: true ,resizable: true,}
].map(c => ({ 
    ...c, // ...defaultColumnProperties 
}));

const rows = [
  { id: 0, title: "Task 1", complete: 20 },
  { id: 1, title: "Task 2", complete: 40 },
  { id: 2, title: "Task 3", complete: 60 },
  { id: 0, title: "Task 1", complete: 20 },
  { id: 1, title: "Task 2", complete: 40 },
  { id: 2, title: "Task 3", complete: 60 },
  { id: 0, title: "Task 1", complete: 20 },
  { id: 1, title: "Task 2", complete: 40 },
  { id: 2, title: "Task 3", complete: 60 },
  { id: 0, title: "Task 1", complete: 20 },
  { id: 1, title: "Task 2", complete: 40 },
  { id: 2, title: "Task 3", complete: 60 },
  { id: 0, title: "Task 1", complete: 20 },
  { id: 1, title: "Task 2", complete: 40 },
  { id: 2, title: "Task 3", complete: 60 }
];

class datagridV extends Component {
  state = { rows };

  onGridRowsUpdated = ({ fromRow, toRow, updated }) => {
    this.setState(state => {
      const rows = state.rows.slice();
      for (let i = fromRow; i <= toRow; i++) {
        rows[i] = { ...rows[i], ...updated };
      }
      return { rows };
    });
  };
constructor(props) {
    super(props);
    this.rowGetter = this.rowGetter.bind(this);
    this.handleGridSort = this.handleGridSort.bind(this);
}

componentDidMount() {
    /*
    document.body.addEventListener('contextmenu', function (evt) {
        // console.log(evt.target.className);
        if (evt.target.className === 'react-grid-HeaderCell-sortable') {
            // ContextMenuTrigger();
            ContextMenuTrigger()
            console.log('Hello');
        }
        evt.preventDefault();
    }, true);
    */
    ReactDOM.findDOMNode(this).addEventListener('nv-event', this._handleNVEvent);
}

componentWillUnmount() {
    ReactDOM.findDOMNode(this).removeEventListener('nv-event', this._handleNVEvent);
}

rowGetter(i) {
    /// console.log('PP',i  );
    return this.state.rows[i]
}

handleGridSort(sortColumn, sortDirection) {
    console.log(sortColumn, sortDirection);
}


  //i => this.state.rows[i]
  render() {
    return (
        <div>
            <ContextMenu id="some_unique_identifier">
        <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
          ContextMenu Item 1
        </MenuItem>
        <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
          ContextMenu Item 2
        </MenuItem>
        <MenuItem divider />
        <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
          ContextMenu Item 3
        </MenuItem>
      </ContextMenu>

      <ReactDataGrid

        columns={columns}
        rowGetter={this.rowGetter}
        rowsCount={12}
        minHeight={200}
        minWidth={200}
        // minColumnWidth={50}
        onGridRowsUpdated={this.onGridRowsUpdated}
        enableCellSelect={true}
        onGridSort={this.handleGridSort}
        contextMenu={
          <ExampleContextMenu
            onRowDelete={(e, { rowIdx }) => setRows(deleteRow(rowIdx))}
            onRowInsertAbove={(e, { rowIdx }) => setRows(insertRow(rowIdx))}
            onRowInsertBelow={(e, { rowIdx }) => setRows(insertRow(rowIdx + 1))}
          />
        }
        // rowRenderer={CustomRowRenderer}
        // RowsContainer={ContextMenuTrigger}
        onColumnResize={(idx, width) =>
            console.log(`Column ${idx} has been resized to ${width}`)
          }

      />
      </div>
    );

  }
}
export default datagridV;
